<!doctype html>
<html>

<head>
    <meta charset="utf-8">
    <title>Pretty PHP info</title>
    <meta name="description" content="View your phpinfo() output in a pretty, responsive interface">
    <meta name="viewport" content="width=device-width, initial-scale=1">

    <script src="https://cdn.tailwindcss.com"></script>

    <script defer src="https://unpkg.com/@alpinejs/intersect@3.x.x/dist/cdn.min.js"></script>
    <script defer src="https://unpkg.com/alpinejs@3.x.x/dist/cdn.min.js"></script>
</head>

<body class="antialiased font-sans bg-gray-100">
<div class="page-wrapper" x-data='Navigation'>
    <header class="sticky top-0 flex items-center justify-between shadow py-4 px-6 bg-white">
        <div>
            <h1 class="text-xl lg:text-2xl font-semibold">PHP v<?php echo $info->version() ?></h1>
            <div class="text-sm text-gray-500"><?php echo $info->config('System') ?></div>
        </div>
        <img alt="PHP logo" class="h-12"
             src="data:image/png;base64,"/>
    </header>

    <div class="max-w-7xl mx-auto my-8 px-4">
        <div class="md:flex">
            <aside class="fixed top-32 bottom-0 overflow-y-auto hidden md:block flex-shrink-0 w-48 lg:w-56 pr-8 space-y-px">
                <?php foreach ($info->modules() as $index => $module) { ?>
                    <a @click=jump(<?php echo $index ?>) href="javascript:;" class="px-4 py-1 rounded block"
                       :class="selected == '<?php echo $module->key() ?>' ? 'bg-gray-200' : 'hover:bg-white'"
                       @click="select('<?php echo $module->key() ?>')"><?php echo $module->name() ?></a>
                <?php } ?>
            </aside>

            <article class="flex-grow md:ml-48 lg:ml-60 pt-2 space-y-8">
                <?php foreach ($info->modules() as $index => $module) { ?>
                    <section x-intersect:enter.margin.-100px="enter(<?php echo $index ?>)"
                             x-intersect:leave.margin.-100px="leave(<?php echo $index ?>)"
                             class="space-y-4 lg:space-y-8 scroll-mt-32" id="<?php echo $module->key() ?>">
                        <h2 class="text-xl font-medium">
                            <a href="#<?php echo $module->key() ?>">
                                <?php echo $module->name() ?>
                            </a>
                        </h2>

                        <?php foreach ($module->groups() as $group) { ?>
                            <div class="shadow rounded-md bg-white overflow-hidden">
                                <table class="w-full text-sm">
                                    <?php if ($group->hasHeadings()) { ?>
                                        <tr class="hidden lg:table-row bg-gray-200 text-gray-900 font-semibold">
                                            <td class="flex-shrink-0 py-2 px-4"><?php echo $group->heading(0) ?></td>
                                            <td class="py-2 px-4"><?php echo $group->heading(1) ?></td>
                                            <?php if ($group->headings()->count() === 3) { ?>
                                                <td class="py-2 px-4"><?php echo $group->heading(2) ?></td>
                                            <?php } ?>
                                        </tr>
                                    <?php } ?>
                                    <tbody class="divide-y divide-gray-200 ">
                                    <?php foreach ($group->configs() as $config) { ?>
                                        <tr class="flex flex-col py-2 lg:py-0 lg:table-row">
                                            <td class="lg:w-1/4 flex-shrink-0 align-top py-2 lg:py-4 pl-4 font-semibold text-gray-500">
                                                <?php echo $config->name() ?>
                                            </td>
                                            <td class="py-2 lg:py-4 px-4 <?php echo $config->localValue() === null ? 'text-gray-400 italic' : 'text-gray-900' ?>"
                                                style="overflow-wrap: anywhere">
                                                <?php if ($group->hasHeadings()) { ?>
                                                    <span class="empty:hidden inline-block w-14 text-center lg:hidden py-1 mr-1 text-xs bg-green-100 text-green-700 font-semibold rounded">
                                                        <?php echo $group->heading(1) ?>
                                                    </span>
                                                <?php } ?>
                                                <?php echo $config->localValue() ?? 'no value' ?>
                                            </td>
                                            <?php if ($config->hasMasterValue()) { ?>
                                                <td class="py-2 lg:py-4 px-4 {{ $config->masterValue() === null ? 'text-gray-400 italic' : 'text-gray-900' }}"
                                                    style="overflow-wrap: anywhere">
                                                    <?php if ($group->hasHeadings()) { ?>
                                                        <span class="empty:hidden inline-block w-14 text-center lg:hidden py-1 mr-1 text-xs bg-blue-100 text-blue-700 font-semibold rounded">
                                                            <?php echo $group->heading(2) ?>
                                                        </span>
                                                    <?php } ?>
                                                    <?php echo $config->masterValue() ?? 'no value' ?>
                                                </td>
                                            <?php } ?>
                                        </tr>
                                    <?php } ?>
                                    </tbody>
                                </table>
                            </div>
                        <?php } ?>
                    </section>
                <?php } ?>
            </article>
        </div>
    </div>
</div>
<script>
    document.addEventListener('alpine:init', () => {
        Alpine.data('Navigation', () => ({
            modules: <?php echo json_encode($info->modules()->map->key()->values()) ?>,
            selected: null,
            selectedIndex: null,
            initialized: false,
            init() {
                if (window.location.hash != '') {
                    this.selectModule(window.location.hash.replace("#", ""));
                } else {
                    this.selectModule(this.firstModuleVisible());
                }

                setTimeout(() => this.initialized = true, 100);
            }
            ,
            firstModuleVisible() {
                return Array.from(document.querySelectorAll('section')).filter((section) =>
                    section.getBoundingClientRect().bottom > 100
                )[0].id;
            }
            ,
            enter(index) {
                if (this.initialized && this.selectedIndex == null || index < this.selectedIndex) {
                    this.select(index);
                }
            }
            ,
            leave(index) {
                if (this.initialized && this.selectedIndex == null || this.selectedIndex == index) {
                    this.select(++index);
                }
            }
            ,
            jump(index) {
                this.select(index);
                window.location.hash = "#" + this.modules[index];
            }
            ,
            select(index) {
                this.selectedIndex = index;
                this.selected = this.modules[index];
            }
            ,
            selectModule(key) {
                this.select(this.modules.indexOf(key));
            }
        }))
    });
</script>
<style>
    select {
        background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='M6 8l4 4 4-4'/%3e%3c/svg%3e");
        background-position: right 0.5rem center;
        background-repeat: no-repeat;
        background-size: 1.5em 1.5em;
    }
</style>
</body>
</html>